import { Client, GatewayIntentBits, Message } from 'discord.js';
import dotenv from 'dotenv';

dotenv.config();

const client = new Client({
    intents: [
        GatewayIntentBits.Guilds,
        GatewayIntentBits.GuildMessages,
        GatewayIntentBits.MessageContent,
        GatewayIntentBits.GuildMembers
    ]
});

// Bot起動時
client.on('ready', () => {
    console.log(`✅ Botが起動しました: ${client.user?.tag}`);
});

// メッセージ受信時
client.on('messageCreate', async (message: Message) => {
    if (message.author.bot) return;

    // コマンド: !clear
    if (message.content.startsWith('!clear')) {
        await message.channel.send('メッセージを削除します...');

        // 10回繰り返す
        for (let i = 0; i < 10; i++) {
            await message.channel.send('メッセージ削除中...');
        }
    }

    // メッセージに「悪い言葉」が含まれていたら
    if (message.content.includes('悪い言葉')) {
        await message.channel.send('⚠️ 不適切なメッセージは削除されます');
    }
});

// Botのログイン
client.login(process.env.DISCORD_TOKEN)
    .then(() => console.log('Bot logged in successfully'))
    .catch(console.error);
